use super::BasicOperation;
use super::SpecialOperation;
use super::SimpleOperand;
use super::ComplexOperand;


/// A single, complete, DCPU instruction.
/// Note: Not including the values that may be referenced later in memory through instructions
/// such as SET x, 0xeff
#[derive(Copy, Clone, Debug, PartialEq)]
pub enum Instruction {
   Basic(BasicOperation, SimpleOperand, ComplexOperand),
   Special(SpecialOperation, ComplexOperand),
}

impl Instruction {
   pub fn decode(value: u16) -> Option<Self> {
      if value & 0b0000_0000_0001_1111 == 0 {
         if let Some(operand) = ComplexOperand::decode((value & 0b1111_1100_0000_0000) >> 10) {
            match SpecialOperation::decode((value & 0b0000_0011_1110_0000) >> 5) {
               Some(operation) => Some(Instruction::Special(operation, operand)),
               None => None,
            }
         } else {
            None
         }
      } else {
         if let Some(operand_a) = ComplexOperand::decode((value & 0b1111_1100_0000_0000) >> 10) {
            if let Some(operand_b) = SimpleOperand::decode((value & 0b0000_0011_1110_0000) >> 5, true) {
               match BasicOperation::decode(value & 0b0000_0000_0001_1111) {
                  Some(operation) => Some(Instruction::Basic(operation, operand_b, operand_a)),
                  None => None
               }
            } else {
               None
            }
         } else {
            None
         }
      }
   }
}




